// LoginForm.js

import React, { useState } from 'react';
import './LoginForm.css'; // Ensure your CSS file is linked

const LoginForm = ({ logo }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    // Handle login logic here
    console.log('Login submitted for:', email);
  };

  return (
    <div className="login-container">
      {logo && <img src={logo} alt="Company Logo" className="company-logo" />}
      <h1>Welcome to FIT INC!</h1>
      <form onSubmit={handleSubmit} className="login-form">
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <div className="forgot-password">
            <a href="#">Forgot password?</a>
          </div>
        </div>
        <button type="submit">Login</button>
        <div className="auth-separator">or</div>
        <button type="button" className="social-login facebook">Continue with Facebook</button>
        <button type="button" className="social-login google">Continue with Google</button>
        <div className="signup-prompt">
          Not with FIT INC yet? <a href="#">Sign up</a>
        </div>
      </form>
    </div>
  );
};

export default LoginForm;
