// LoginForm.js

import React, { useState } from 'react';
import './LoginForm.css'; // import CSS

const LoginForm = ({ logo }) => {
  const [isLogin, setIsLogin] = useState(true); // Toggle between login and register
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState(''); // For the selected role

  const handleSubmit = (event) => {
    event.preventDefault();
    // Handle login or registration logic here
    console.log('Submitted for:', username, ' Role:', role);
  };

  return (
    <div className="login-container">
      {logo && <img src={logo} alt="Company Logo" className="company-logo" />}
      <h2>{isLogin ? 'Login' : 'Register'}</h2>
      <form onSubmit={handleSubmit} className="login-form">
        <div className="form-group">
          <label htmlFor="username">Username</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        {!isLogin && (
          <div className="form-group">
            <label htmlFor="role">Role</label>
            <select id="role" value={role} onChange={(e) => setRole(e.target.value)}>
              <option value="">Select a role</option>
              <option value="Fitness Professional">Fitness Professional</option>
              <option value="Client">Client</option>
              <option value="Admin Team">Admin Team</option>
            </select>
          </div>
        )}
        <button type="submit">{isLogin ? 'Login' : 'Register'}</button>
        <button type="button" onClick={() => setIsLogin(!isLogin)}>
          {isLogin ? 'Need to register?' : 'Back to login'}
        </button>
      </form>
    </div>
  );
};

export default LoginForm;
